parameters.jsCode:
// Get data from previous nodes
const aiOutput = $input.item.json;
const aiResponse =
  aiOutput.output ||
  aiOutput.text ||
  aiOutput.response ||
  "No analysis generated";
const perplexityData = $("Get Current News Context").item.json;
const userQuestion = $("Normalize Input").item.json.chatInput;

// Extract citations safely
let citations = [];
if (perplexityData.citations && Array.isArray(perplexityData.citations)) {
  citations = perplexityData.citations;
}

// Extract token usage if available
let tokenUsage = {};
if (perplexityData.usage) {
  tokenUsage.perplexity = perplexityData.usage;
}

// Format the response
const formattedResponse = {
  question: userQuestion,
  analysis: aiResponse,
  metadata: {
    news_sources: citations,
    timestamp: new Date().toISOString(),
    model: "gpt-4o",
    mcp_tools_used: ["OpenAlex (250M+ scholarly works, citations, authors)"],
    perplexity_model:
      perplexityData.model || "llama-3.1-sonar-large-128k-online",
    token_usage: tokenUsage,
  },
};

// Return formatted data
return [
  {
    json: formattedResponse,
  },
];

// Normalizes incoming payloads (JSON, form, plain text) into { chatInput, threadId, client:{} }
// so downstream nodes can safely use {{$json.chatInput}}.

const headers = $json.headers ?? {};
const ct = String(
  headers["content-type"] || headers["Content-Type"] || "",
).toLowerCase();

let body = $json.body ?? {};
let query = $json.query ?? {};
let chatInput = "";

// 1) Try common JSON/form keys
function pick(o) {
  if (!o) return "";
  return o.chatInput ?? o.message ?? o.text ?? o.q ?? o.prompt ?? "";
}

if (ct.includes("application/json")) {
  chatInput = pick(body);
} else if (
  ct.includes("application/x-www-form-urlencoded") ||
  ct.includes("multipart/form-data")
) {
  chatInput = pick(body);
} else if (ct.includes("text/plain")) {
  // For raw text bodies
  chatInput = String($json.body ?? "").trim();
}

// 2) Fallbacks: query string and root-level JSON
if (!chatInput) chatInput = pick(query);
if (!chatInput) chatInput = String($json.chatInput ?? "").trim();

// 3) Final trim and guardrail
chatInput = String(chatInput || "").trim();
if (!chatInput) {
  // You can also throw to short-circuit the workflow:
  // throw new Error('Missing chatInput');
  return [{ json: { error: "Missing chatInput", received: $json } }];
}

// Optional threading and client metadata
const threadId =
  body.threadId ?? query.threadId ?? headers["x-thread-id"] ?? null;

return [
  {
    json: {
      chatInput,
      threadId,
      client: {
        ip: headers["x-forwarded-for"] ?? headers["cf-connecting-ip"] ?? null,
        ua: headers["user-agent"] ?? null,
        origin: headers.origin ?? null,
        referer: headers.referer ?? null,
      },
    },
  },
];
