parameters.jsCode:
// Get data from previous nodes
const aiOutput = $input.item.json;
const aiResponse =
  aiOutput.output ||
  aiOutput.text ||
  aiOutput.response ||
  "No analysis generated";
const perplexityData = $("Get Current News Context").item.json;
const userQuestion = $("Normalize Input").item.json.chatInput;

// Extract citations safely
let citations = [];
if (perplexityData.citations && Array.isArray(perplexityData.citations)) {
  citations = perplexityData.citations;
}

// Extract token usage if available
let tokenUsage = {};
if (perplexityData.usage) {
  tokenUsage.perplexity = perplexityData.usage;
}

// Format the response
const formattedResponse = {
  question: userQuestion,
  analysis: aiResponse,
  metadata: {
    news_sources: citations,
    timestamp: new Date().toISOString(),
    model: "gpt-4o",
    mcp_tools_used: ["OpenAlex (250M+ scholarly works, citations, authors)"],
    perplexity_model:
      perplexityData.model || "llama-3.1-sonar-large-128k-online",
    token_usage: tokenUsage,
  },
};

// Return formatted data
return [
  {
    json: formattedResponse,
  },
];

// Normalizes incoming payloads (JSON, form, plain text) into
// { chatInput, history, auth_user_id, threadId, client:{} }
// so downstream nodes receive consistent structures.

const headers = $json.headers ?? {};
const ct = String(
  headers["content-type"] || headers["Content-Type"] || ""
).toLowerCase();

const body = $json.body ?? {};
const query = $json.query ?? {};
let chatInput = "";

// Helper to pluck a prompt-like field from an object
const pick = (source) => {
  if (!source || typeof source !== "object") return "";
  return (
    source.chatInput ??
    source.message ??
    source.text ??
    source.q ??
    source.prompt ??
    ""
  );
};

if (ct.includes("application/json")) {
  chatInput = pick(body);
} else if (
  ct.includes("application/x-www-form-urlencoded") ||
  ct.includes("multipart/form-data")
) {
  chatInput = pick(body);
} else if (ct.includes("text/plain")) {
  chatInput = String($json.body ?? "").trim();
}

if (!chatInput) chatInput = pick(query);
if (!chatInput) chatInput = String($json.chatInput ?? "").trim();

chatInput = String(chatInput || "").trim();
if (!chatInput) {
  return [{ json: { error: "Missing chatInput", received: $json } }];
}

// Normalize optional history array
const rawHistory = Array.isArray(body.history)
  ? body.history
  : Array.isArray(query.history)
    ? query.history
    : Array.isArray($json.history)
      ? $json.history
      : [];

const history = rawHistory
  .filter((entry) => entry && typeof entry.content === "string")
  .map((entry) => ({
    role: entry.role === "assistant" ? "assistant" : "user",
    content: entry.content.trim(),
  }))
  .filter((entry) => entry.content.length > 0);

const authUserId =
  body.auth_user_id ??
  query.auth_user_id ??
  headers["x-auth-user-id"] ??
  headers["X-Auth-User-Id"] ??
  ($json.auth_user_id ?? null);

const threadId =
  body.threadId ??
  query.threadId ??
  headers["x-thread-id"] ??
  headers["X-Thread-Id"] ??
  null;

return [
  {
    json: {
      chatInput,
      history,
      auth_user_id: typeof authUserId === "string" ? authUserId : null,
      threadId,
      client: {
        ip:
          headers["x-forwarded-for"] ??
          headers["X-Forwarded-For"] ??
          headers["cf-connecting-ip"] ??
          headers["CF-Connecting-IP"] ??
          null,
        ua: headers["user-agent"] ?? headers["User-Agent"] ?? null,
        origin: headers.origin ?? headers.Origin ?? null,
        referer: headers.referer ?? headers.Referer ?? null,
      },
    },
  },
];
