const postMeta = postMetaItems[0]?.json ?? {};

const requestId = postMeta.request_id || normalize.request_id;
const authUserId = postMeta.auth_user_id || normalize.auth_user_id;

if (!requestId || !authUserId) {
  throw new Error(
    "Missing request_id or auth_user_id for completion payload (post branch)",
  );
}

const postItems = safeItems("Upsert Post");
const commentItems = safeItems("Upsert Comments");

const posts = postItems
  .map((item) => item.json.id ?? item.json.post_id ?? item.json.full_url)
  .filter(Boolean);
const comments = commentItems
  .map((item) => item.json.id ?? item.json.comment_id ?? item.json.full_url)
