];

// Output: [{ json: { post_url, post_content, page_url } }, ...]

function cleanUrl(u) {
  return String(u ?? '').replace(/[?#].*$/, '');
}

const unique = new Map();

// Build a lookup: post_url → page_url from the previous node (if it ran)
let pageByPost = new Map();
try {
  const prevItems = $('Extract Posts Data').all().map(i => i.json).filter(Boolean);
  if (prevItems.length) {
    pageByPost = new Map(
      prevItems
        .filter(x => x.post_url)
        .map(x => [cleanUrl(x.post_url), x.page_url ?? null])
    );
  }
} catch (e) {
  // Node may not exist or may not have run; ignore and default to null page_url
}

// Collect unique posts from "Extract Post Data"
for (const item of $('Extract Post Data').all()) {
  const { post_url, post_content, posted_at } = item.json;
  if (!post_url) continue;

  const key = cleanUrl(post_url);
  if (!unique.has(key)) {
    unique.set(key, {
      post_url,
      post_content: post_content ?? null,
      page_url: pageByPost.get(key) ?? null,
      posted_at: posted_at
    });
  }
}

// Return as n8n items
return Array.from(unique.values()).map(v => ({ json: v }));

// Deduplicate users from the incoming comment rows
// Input: items from "Extract Post Data" (each has user_profile_id, user_full_name)
// Output: [{ json: { user_profile_id, user_full_name } }, ...] unique by user_profile_id

function normId(id) {
  return String(id ?? '').trim().toLowerCase(); // handles numeric & pfbid* strings
}

const byId = new Map();

for (const { json } of $input.all()) {
  const idRaw = json.user_profile_id;
  const id = normId(idRaw);
  if (!id) continue;

  const name = (json.user_full_name ?? '').trim() || null;

  if (!byId.has(id)) {
    byId.set(id, { user_profile_id: idRaw, user_full_name: name });
  } else {
    // Prefer a non-empty name if we didn't have one yet
    const cur = byId.get(id);
    if (!cur.user_full_name && name) cur.user_full_name = name;
  }
}

// Return one n8n item per unique user
return Array.from(byId.values()).map(u => ({ json: u }));

const urls = [];

for (const item of $("Canonicalize Post URL").all()) {
  urls.push(item.json.post_urls);
}

return { startUrls: urls[0] };

// Build startUrls from the "Extract Posts Data" node

function cleanUrl(u) {
  return String(u ?? '').trim().replace(/[?#].*$/, '');
}

const rows = $('Extract Posts Data').all().map(i => i.json);
const seen = new Set();
const startUrls = [];

for (const r of rows) {
  const url = cleanUrl(r.post_url);
  if (!url) continue;
  if (seen.has(url)) continue;
  seen.add(url);
  startUrls.push(url);
}

// Return one n8n item
return [{ json: { startUrls } }];

// Node: Build Array Of Post URLs (fix)
// Input: { post_urls: [...] } (from Normalize)
// Output: Items: [{json:{post_url: <canonical>}}, ...]
function cleanFacebookPostUrl(raw){
  if(!raw) return null;
  let s = String(raw).trim();
  if(!/^https?:\/\//i.test(s)) s = 'https://' + s;
  s = s.replace(/^http:\/\//i,'https://');
  s = s.replace(/[?#].*$/,'');
  if(!/^https:\/\/(www\.)?facebook\.com\//i.test(s)) return null;
  if(!/\/$/.test(s)) s += '/';
  return s;
}

const posts = Array.isArray($json.post_urls) ? $json.post_urls : [];
return posts
  .map(u => cleanFacebookPostUrl(u))
  .filter(Boolean)
  .map(u => ({ json: { ...$json, post_url: u } }));

