const HOURS = 3;
const lastStr = $node["Latest Insert For Post"].json?.inserted_at ?? null;
const now = new Date();
const windowMs = HOURS * 3600000;
const newer = lastStr ? new Date(lastStr) : new Date(now.getTime() - windowMs);
const older = now;
