  d ? new Date(d).toISOString().replace(/\.\d{3}Z$/, "Z") : null;
const sinceIso = fmt(newer);
const untilIso = fmt(older);
let can_scrape = false,
  reason = "";
if (!lastStr) {
  can_scrape = true;
  reason = "no previous ingestion watermark";
} else {
  const elapsed = now - lastStr;
  if (now - new Date(lastStr) > windowMs) {
    can_scrape = true;
    reason = "elapsed > policy";
  } else {
    const minsLeft = Math.ceil((windowMs - (now - new Date(lastStr))) / 60000);
    reason = `too recent; ${minsLeft} min left`;
  }
}
