function cleanFacebookPageUrl(raw) {
  if (!raw) return null;
  let s = String(raw).trim();
  if (!/^https?:\/\//i.test(s)) s = "https://" + s;
  s = s.replace(/^http:\/\//i, "https://");
  s = s.replace(/[?#].*$/, "");
  if (!/^https:\/\/(www\.)?facebook\.com\//i.test(s)) return null;
  if (!/\/$/.test(s)) s = s + "/";
  return s;
}
const out = [];
for (const it of $input.all()) {
  out.push({
    json: { ...it.json, page_url: cleanFacebookPageUrl(it.json.page_url) },
  });
}
return out;

const pages = Array.isArray($json.page_urls) ? $json.page_urls : [];
const posts = Array.isArray($json.post_urls) ? $json.post_urls : [];
const errors = [];
if (!pages.length && !posts.length)
  errors.push("Provide page_urls or post_urls");
if (
  !$json.auth_user_id ||
  typeof $json.auth_user_id !== "string" ||
  !$json.auth_user_id.trim()
) {
  errors.push("auth_user_id is required");
}
if (pages.length && posts.length)
  errors.push("Provide only one of page_urls or post_urls");
for (const u of pages)
  if (typeof u !== "string" || !u.trim())
    errors.push("page_urls[] must be non-empty strings");
for (const u of posts)
  if (typeof u !== "string" || !u.trim())
    errors.push("post_urls[] must be non-empty strings");
return {
  json: {
    ...$json,
    mode: pages.length ? "page" : "post",
    errors,
    valid: errors.length === 0,
  },
};

// Group items by page_url and collect unique post URLs per page
function cleanUrl(u) {
  return String(u ?? '').trim();
}

const pagesByUrl = new Map();

for (const { json } of $input.all()) {
  const page_url = cleanUrl(json.page_url);
  if (!page_url) continue;

  // Initialize page entry (with an internal Set for dedupe)
  if (!pagesByUrl.has(page_url)) {
    pagesByUrl.set(page_url, {
      page_url,
      page_name: json.page_name ?? null,
      _post_urls_set: new Set(), // internal; will be turned into array later
    });
  }

  // Prefer the first non-empty page_name or update if current is null/blank
  const entry = pagesByUrl.get(page_url);
  const incomingName = cleanUrl(json.page_name);
  if (incomingName && !entry.page_name) {
    entry.page_name = incomingName;
  }

  // Collect post_url uniquely
  const post_url = cleanUrl(json.post_url);
  if (post_url) {
    entry._post_urls_set.add(post_url);
  }
}

// Emit one item per page with post_urls as an array
const result = Array.from(pagesByUrl.values()).map(p => ({
  json: {
    page_url: p.page_url,
    page_name: p.page_name, // convert Set -> array
  }
}));

return result;

// Join post IDs (from Upsert Post1) + user IDs (from Upsert Users)
// onto each original comment row (from Extract Post Data).

function cleanUrl(u) {
  return String(u ?? '').replace(/[?#].*$/, '');
}
function normFbId(id) {
  return String(id ?? '').trim().toLowerCase();
}

// 1) Posts returned by Upsert Post1 (this node's input)
const posts = $input.all().map(i => i.json);

// 2) Users returned by Upsert Users node
const users = $('Upsert Users').all().map(i => i.json);

// 3) Original comment rows (from Extract Post Data)
const comments = $('Extract Post Data').all().map(i => i.json);

// 4) Index posts by normalized URL
const postsByUrl = new Map();
for (const p of posts) {
  const key = cleanUrl(p.full_url);
  if (key) postsByUrl.set(key, p); // { id, full_url, content, ... }
}

// 5) Index users by normalized fb_profile_id
const usersByFbId = new Map();
for (const u of users) {
  const key = normFbId(u.fb_profile_id);
  if (key && !usersByFbId.has(key)) usersByFbId.set(key, u); // { id, fb_profile_id, full_name }
}

// 6) Build output rows for comments with post_id + user_id (+ posted_at)
const out = [];
for (const c of comments) {
  const postKey = cleanUrl(c.post_url);
  const post = postsByUrl.get(postKey);
  if (!post) continue; // skip if we didn't upsert/return this post

  const user = usersByFbId.get(normFbId(c.user_profile_id));

  out.push({
    json: {
      post_id: post.id,
      post_url: post.full_url,
      post_content: post.content ?? null,

      user_id: user?.id ?? null,
      user_fb_profile_id: user?.fb_profile_id ?? c.user_profile_id ?? null,
      user_full_name: user?.full_name ?? c.user_full_name ?? null,

      comment_url: c.comment_url ?? null,
      comment_content: c.comment_content ?? null,

      // pulled from Extract Post Data row
      posted_at: c.posted_at ?? null,
    }
  });
}

return out;
